async function handleRequest(request) {
  const url = new URL(request.url)
  const path = url.pathname

  // å®šä¹‰ç¯å¢ƒå˜é‡ GITHUB_USERNAME å’Œ GITHUB_PAT
  const GITHUB_USERNAME = 'zbb7001' // æ›¿æ¢ä¸ºä½ çš„ GitHub ç”¨æˆ·å
  const GITHUB_PAT = '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' // æ›¿æ¢ä¸ºä½ çš„ GitHub PATï¼Œåªå¼€æ”¾ repo æƒé™å³å¯

  // æ„å»º GitHub raw å†…å®¹çš„ URL
  const githubUrl = `https://raw.githubusercontent.com/${GITHUB_USERNAME}${path}`

  // åˆ›å»ºæ–°çš„è¯·æ±‚ï¼Œæ·»åŠ å¿…è¦çš„å¤´éƒ¨
  const modifiedRequest = new Request(githubUrl, {
    method: request.method,
    headers: {
      ...request.headers,
      'Authorization': `token ${GITHUB_PAT}`,
      'Accept': 'application/vnd.github.v3.raw'
    }
  })

  // å‘é€è¯·æ±‚å¹¶è¿”å›å“åº”
  try {
    const response = await fetch(modifiedRequest)

    // å¦‚æœå“åº”ä¸æˆåŠŸï¼ŒæŠ›å‡ºé”™è¯¯
    if (!response.ok) {
      throw new Error(`GitHub API responded with ${response.status}: ${response.statusText}`)
    }

    // åˆ›å»ºæ–°çš„å“åº”ï¼Œä¿ç•™åŸå§‹å†…å®¹ä½†ç§»é™¤æ•æ„Ÿå¤´éƒ¨
    const newResponse = new Response(response.body, response)
    newResponse.headers.delete('Authorization')

    return newResponse
  } catch (error) {
    return new Response(`Error: ${error.message}`, { status: 500 })
  }
}

